'use strict';

//для больших скриптов лучше пользовать multiline.js

var bash_create = require('ice_bash_exec')(__dirname);


var get_suggestions = bash_create(['DICTIONARY', 'LINES'], 'echo "$DICTIONARY"-"$LINES"');

get_suggestions('vocab_nouns.txt', 10)
.then(function(stdout) {
  process.stdout.write(stdout);
})
.catch(function(e){
  console.error(e);
});
