'use strict';
var ice_worker = require('ice_worker');
var q = require('ice_q');

ice_worker.register_worker('hello', function(a,b,c){
  
  //console.log('WORKER 2', a, b, c);
  
  var deferred = q.defer();
  
  setTimeout(function(){
    deferred.resolve([a,b,c]);
  }, 5);

  return deferred.promise;
});
