'use strict';
var ice_worker = require('ice_worker');
var q = require('ice_q');

/**
 * 
 */
ice_worker.register_worker('hello', {prefetchCount:6}, function(a,b,c){
  
  
  var deferred = q.defer();
  
  setTimeout(function(){
    console.log('WORKER OPTS', a, b, c);
    deferred.resolve([a,b,c]);
  }, 5);

  return deferred.promise;

});
